button.btn.small {
    background: #F5C518;
    border: unset;
    padding: 4px 10px;
    border-radius: 2px;
    transition: background .2s linear;
}
button.btn.small:hover {
    background: #BE9500;
}
.ab_widget .seemore::after {
	display: none;
}
.btn.primary {
	background: #F5C518;
    border-radius: 2px;
    transition: background .2s linear;
}
.btn2.small {
    background: #F5C518;
    color: #000;
    border-radius: 2px;
    border: 1px solid transparent;
    padding: 3px 8px;
    transition: background .2s linear;
}
.btn2.small:hover,
.btn.primary:hover {
	background: #BE9500;
}
