    box-shadow: 0px 2px 4px 0px rgba(0, 0, 0, 0.45);
    transition: .2s background-color linear, box-shadow .2s linear;
}
.see-all a:hover {
    box-shadow: 0px 2px 4px 2px rgba(0, 0, 0, 0.45);
    background-color: #BE9500;
